
public class que5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("PART A");
		for(int i =3;i>0;i--) {
			for(int j=0;j<i;j++) {
				System.out.print("#");
			}
			System.out.println();
		}
		
		System.out.println("PART B");
		for(int i =1;i<=5;i++) {
			for(int j=0;j<i;j++) {
				System.out.print(".");
			}
			System.out.println();
		}
		
	}

}
