
public class que7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a;
	    //System.out.println("The series is:");
	    for(a=1;a<=13;a=a+3) {
	    	System.out.print(a +" ");
	    }
	    
	    int b;
	    //System.out.println("The series is:");
	    for(b=-20;b<50;b=b+5) {
	    	System.out.print(b +" ");
	    }
	    
	    int c=1,d=-2;
	    //System.out.println("The series is:");
	    while(c<10 && d>-11){
	    		System.out.print(c +" ");
	    		System.out.print(d +" ");
	    		c=c+2;
	    		d=d-2;
	    		
	    	
	    }
	    
	    
	}

}
